akApp.controller('listaCtrl', ['$scope', '$http', '$sce', function ($scope, $http, $sce) {
    console.log("listaCtrl");
    console.log(productos);
    console.log(rubros);

    $scope.$watch('query', function(newValue, oldValue) {
        console.log(newValue);
    });

    $scope.productos = productos;
    $scope.rubros = rubros;
    $scope.query = "";
    $scope.trustAsHtml = $sce.trustAsHtml;

    function removeAccents(value) {
        return value
            .replace(/á/g, 'a')
            .replace(/é/g, 'e')
            .replace(/í/g, 'i')
            .replace(/ó/g, 'o')
            .replace(/ú/g, 'u')
            .replace(/Á/g, 'A')
            .replace(/É/g, 'E')
            .replace(/Í/g, 'I')
            .replace(/Ó/g, 'O')
            .replace(/Ú/g, 'U')
            .replace(/ñ/g, 'n')
            .replace(/Ñ/g, 'N');

    }

    $scope.ignoreAccentsProducto = function (item) {
        if (!$scope.query)
            return true;

        var fullItem = item.producto + ' ' + item.rubro + ' ' + item.marca+ ' ' + item.nivel;
        var text = removeAccents(fullItem.toLowerCase());
        var search = removeAccents($scope.query.toLowerCase());
        var searchTextSplit = search.split(' ');
        
        for (var y = 0; y < searchTextSplit.length; y++) {
            if (text.indexOf(searchTextSplit[y]) == -1) {
                return false;
            }
        }
        return true;
    };

    $scope.ignoreAccentsRubro = function (item) {
        if (!$scope.query)
            return true;

        var fullItem = item.title;
        var text = removeAccents(fullItem.toLowerCase());
        var search = removeAccents($scope.query.toLowerCase());
        var searchTextSplit = search.split(' ');
        
        for (var y = 0; y < searchTextSplit.length; y++) {
            if (text.indexOf(searchTextSplit[y]) !== -1) {
                return true;
            }
        }
        return false;
    };

    $scope.renderHtml = function (html_code) {
        return $sce.trustAsHtml(html_code);
    };

    $scope.clickRubro = function (rubro) {
        console.log(rubro.title);
        $scope.query = rubro.title;
    };
}]);




akApp.directive('countUp', ['$compile', function ($compile, $timeout) {
    return {
        restrict: 'E',
        replace: false,
        scope: {
            countTo: "=countTo",
            interval: '=interval'
        },
        controller: ['$scope', '$element', '$attrs', '$timeout', function ($scope, $element, $attrs, $timeout) {
            $scope.millis = 0;
            if ($element.html().trim().length === 0) {
                $element.append($compile('<span>{{millis}}</span>')($scope));
            } else {
                $element.append($compile($element.contents())($scope));
            }

            var i = 0;
            function timeloop() {
                setTimeout(function () {
                    $scope.millis++;
                    $scope.$digest();
                    i++;
                    if (i < $scope.countTo) {
                        timeloop();
                    }
                }, $scope.interval)
            }
            timeloop();
        }]
    }
}]);


